/* eslint-disable @gechiui/i18n-text-domain, @gechiui/i18n-translator-comments */

/**
 * GeChiUI dependencies
 */
import { __, _x, _n, _nx } from '@gechiui/i18n';
import { addFilter } from '@gechiui/hooks';

describe( 'i18n filters', () => {
	test( 'Default i18n functions call filters', () => {
		addFilter( 'i18n.gettext', 'tests', () => {
			return 'goodbye';
		} );
		expect( __( 'hello' ) ).toBe( 'goodbye' );
		addFilter( 'i18n.gettext_with_context', 'tests', () => {
			return 'goodbye';
		} );
		expect( _x( 'hello', 'context' ) ).toBe( 'goodbye' );
		addFilter(
			'i18n.ngettext',
			'tests',
			( translation, singular, plural, count ) => {
				if ( count === 1 ) {
					return 'goodbye';
				}
				return 'goodbyes';
			}
		);
		expect( _n( '嗨', '嗨', 1 ) ).toBe( 'goodbye' );
		expect( _n( '嗨', '嗨', 2 ) ).toBe( 'goodbyes' );
		addFilter(
			'i18n.ngettext_with_context',
			'tests',
			( translation, singular, plural, count ) => {
				if ( count === 1 ) {
					return 'goodbye';
				}
				return 'goodbyes';
			}
		);
		expect( _nx( '嗨', '嗨', 1, 'context' ) ).toBe( 'goodbye' );
		expect( _nx( '嗨', '嗨', 2, 'context' ) ).toBe( 'goodbyes' );
	} );
} );

/* eslint-enable @gechiui/i18n-text-domain, @gechiui/i18n-translator-comments */
